   function encryptMD5(message) {
  // Mã hoá MD5
  var encryptedMessage = CryptoJS.MD5(message);

  // Trả về kết quả dưới dạng chuỗi hex
  return encryptedMessage.toString(CryptoJS.enc.Hex);
}

